function onFormSubmit(e) {
  // Get the form responses
  const responses = e.values; // This returns an array of responses
  const fname = responses[1];  // Adjust index based on your form structure
  const lname = responses[2];
  const company = responses[3];
  const contact = responses[4];

  // Prepare the AJAX request to your Flask app
  const url = ' https://4323-50-227-148-194.ngrok-free.app/submit'; // Replace with your Ngrok URL

  const payload = {
    'fname': fname,
    'lname': lname,
    'company': company,
    'contact': contact
  };

  const options = {
    'method': 'POST',
    'contentType': 'application/json',
    'payload': JSON.stringify(payload)
  };

  // Send the AJAX request
  UrlFetchApp.fetch(url, options);
}

// Create a trigger for form submission
function createTrigger() {
  const form = FormApp.openById('YOUR_FORM_ID'); // Replace with your Google Form ID
  ScriptApp.newTrigger('onFormSubmit')
    .forForm(form)
    .onFormSubmit()
    .create();
}
